mapper.insert:
INSERT INTO SHOP_ITEM (
    ITEM_CODE
    , ITEM_NAME
    , ITEM_PRICE
    , ITEM_INTRO
    , CATE_CODE
) VALUES (
    #{itemCode}
    , #{itemName}
    , #{itemPrice}
    , #{itemIntro}
    , #{cateCode}
)

INSERT INTO ITEM_IMAGE (
    ORIGIN_FILE_NAME
    , ATTACHED_FILE_NAME
    , IS_MAIN
    , ITEM_CODE
) VALUES

(
    #{img.originFileName}
    , #{img.attachedFileName}
    , #{img.isMain}
    , #{img.itemCode}
)

mapper.select:
SELECT BUY_CODE , MEMBER_ID , BUY_PRICE , BUY_DATE FROM shop_buy WHERE 1=1
AND ${searchType} LIKE CONCAT('%',#{searchValue},'%')
AND DATE_FORMAT(BUY_DATE, '%Y-%m-%d')>=#{fromDate}
AND DATE_FORMAT(BUY_DATE, '%Y-%m-%d')<=#{toDate}
ORDER BY BUY_DATE DESC

mapper.update:
UPDATE SHOP_ITEM
SET
ITEM_NAME=#{itemName}
, ITEM_STOCK=#{itemStock}
, ITEM_STATUS=#{itemStatus}
, CATE_CODE= #{cateCode}
WHERE ITEM_CODE=#{itemCode}
